import{_ as a,o as t,c as o,a as n,b as r,w as i,e as s,d as e,r as d}from"./app.40268f25.js";var h="/English-level-up-tips/assets/\u9ED1\u4EBA\u95EE\u53F7.jpg";const c={},l=s('<h1 id="\u626F\u6DE1\u7BC7" tabindex="-1"><a class="header-anchor" href="#\u626F\u6DE1\u7BC7" aria-hidden="true">#</a> \u626F\u6DE1\u7BC7</h1><h2 id="\u804A\u804A\u56FD\u5185\u7684it\u57F9\u8BAD" tabindex="-1"><a class="header-anchor" href="#\u804A\u804A\u56FD\u5185\u7684it\u57F9\u8BAD" aria-hidden="true">#</a> \u804A\u804A\u56FD\u5185\u7684IT\u57F9\u8BAD</h2><p>\u8FD1\u51E0\u5E74\u56FD\u5185\u4E92\u8054\u7F51\u884C\u4E1A\u7684\u5FEB\u901F\u53D1\u5C55\uFF0C\u8BA9\u8FD9\u4E2A\u884C\u4E1A\u6210\u4E3A\u65F6\u4E0B\u6700\u70ED\u95E8\u4E5F\u662F\u6700\u4EE4\u4EBA\u8BEF\u89E3\u7684\u884C\u4E1A\u4E4B\u4E00\u3002 \u51B2\u7740\u53EF\u89C2\u7684\u85AA\u6C34\uFF0C\u6709\u5927\u91CF\u5176\u4ED6\u884C\u4E1A\u7684\u4EBA\u9009\u62E9\u8FDB\u5165\u4E92\u8054\u7F51\u9886\u57DF\u3002 \u5982\u679C\u4F60\u53EA\u662F\u56E0\u4E3A\u8FD9\u4E2A\u884C\u4E1A\u7684\u85AA\u8D44\u6C34\u5E73\u76EE\u524D\u76F8\u8F83\u4E8E\u4F60\u4ECE\u4E8B\u7684\u884C\u4E1A\u504F\u9AD8\uFF0C\u800C\u5B9E\u9645\u4E0A\u4F60\u5BF9\u8FD9\u4E2A\u884C\u4E1A\u6CA1\u6709\u4E00\u4E1D\u4E00\u6BEB\u7684\u70ED\u7231\uFF0C \u90A3\u4E48\u4F60\u5F53\u521D\u600E\u4E48\u6025\u5207\u7684\u8FDB\u5165\u8FD9\u4E2A\u884C\u4E1A\uFF0C\u4EE5\u540E\u5C31\u4F1A\u591A\u4E48\u75DB\u82E6\u5730\u79BB\u5F00\u3002</p><p>\u6C38\u8FDC\u7EFC\u5408\u8003\u8651 <strong>\u559C\u6B22\u4E14\u80FD\u80DC\u4EFB\u7684\u5DE5\u4F5C</strong>\uFF0C\u6211\u5E0C\u671B\u5927\u5BB6\u90FD\u80FD\u8FC7\u4E0A\u81EA\u5DF1\u671F\u5F85\u7684\u751F\u6D3B\uFF0C\u800C\u4E0D\u662F\u4E3A\u4E86\u751F\u5B58\u800C\u653E\u5F03\u751F\u6D3B\u3002</p><p>\u6015\u4F24\u53CA\u56FD\u5185\u67D0\u4E9B\u673A\u6784\u7684\u5229\u76CA\uFF0C\u6458\u4E00\u6BB5\u300A\u524D\u7AEF\u5F00\u53D1\u8005\u624B\u518C2017\u300B\u91CC\u7684\u8BDD(\u5220\u6389\u4E86\u70B9\u540D\u7684\u673A\u6784)\uFF1A</p><blockquote><p>Lately a lot of non-accredited, expensive, front-end code schools/bootcamps have emerged. These avenues of becoming a front-end developer are typically teacher directed courses, that follow a more traditional style of learning, from an official instructor. Keep in mind, if you are considering an expensive training program, this is the web! Everything you need to learn is on the web for the taking, costing little to nothing. However, if you need someone to tell you how to take and learn what is actually free, and hold you accountable for learning it, you might consider an organized course. Otherwise, I am not aware of any other profession that is practically free for the taking with an internet connection, a hundred dollars a month for screencasting memberships, and a burning desire for knowledge.</p></blockquote><p>--\u300AFront-end Developer Handbook 2017 (Kindle \u4F4D\u7F6E 810-813). GitBook\u300B</p><p>\u6240\u4EE5\u5BF9\u4E8E\u60F3\u8981\u901A\u8FC7\u57F9\u8BAD\u6765\u8FDB\u5165\u8FD9\u4E2A\u884C\u4E1A\u7684\u4EBA\uFF0C\u6211\u7684\u5EFA\u8BAE\u662F\uFF1A</p><p>\u4F60\u60F3\u4ECE\u4E8B\u7684\u662F\u4E92\u8054\u7F51\u884C\u4E1A\uFF0C\u5E94\u8BE5\u5177\u5907\u5229\u7528\u4E92\u8054\u7F51\u7684\u610F\u8BC6\uFF0C\u628A\u9AD8\u6602\u7684\u8D39\u7528\u7ED9\u81EA\u5DF1\u4E70\u4E9B\u597D\u73A9\u7684\u5427\u3002</p><blockquote><p>\u5982\u679C\u4F60\u662F\u5728\u6821\u5B66\u751F\uFF0C\u57F9\u8BAD\u5355\u4F4D\u6002\u607F\u4F60\u529E\u7406\u7C7B\u4F3C\u8D37\u6B3E\uFF08\u5206\u671F\u8FD8\u6B3E\uFF09\u7684\u4E1A\u52A1\uFF0C\u8BF7\u52A1\u5FC5\u8981\u614E\u91CD\uFF0C\u90A3\u4E9B\u5355\u4F4D\u771F\u7684\u662F\u6709\u4E9B\u574F\uFF01</p></blockquote><h2 id="it\u57F9\u8BAD\u9760\u8C31\u5417" tabindex="-1"><a class="header-anchor" href="#it\u57F9\u8BAD\u9760\u8C31\u5417" aria-hidden="true">#</a> IT\u57F9\u8BAD\u9760\u8C31\u5417\uFF1F</h2><p>\u6211\u82B1\u4E864\u4E2A\u6708\u65F6\u95F4 2\u4E07\u5757\u94B1\u57F9\u8BAD\u4E86web\u524D\u7AEF\u5F00\u53D1</p><p>\u521A\u57F9\u8BAD\u5B8C\u4E24\u4E2A\u661F\u671F\u6211\u5C31\u6536\u5230\u4E86\u7F8E\u56E2\u7F51\u7684offer</p><p>\u6211\u627F\u8BA4\u6211\u4E0D\u662F\u73ED\u91CC\u5B66\u4E60\u6700\u597D\u7684</p><p>\u4F46\u6211\u5374\u662F\u73ED\u91CC\u7B2C\u4E00\u4E2A\u627E\u5230\u5DE5\u4F5C\u7684</p><p>\u800C\u4E14\u8FD8\u662F\u4E2A\u5927\u5382</p><p>\u6211\u4E00\u76F4\u76F8\u4FE1 \u52E4\u80FD\u8865\u62D9 \u53EA\u8981\u6709\u51B3\u5FC3 \u4EC0\u4E48\u4E8B\u90FD\u662F\u53EF\u4EE5\u505A\u5230\u7684</p><p>\u4ECA\u5929\u5165\u804C\u4E00\u4E2A\u661F\u671F\u4E86</p><p>\u516C\u53F8\u7684\u4EBA\u5BF9\u6211\u90FD\u5F88\u597D \u8FD8\u7ED9\u6211\u914D\u4E86 \u7535\u52A8\u8F66 \u548C\u5934\u76D4 \u8FD8\u6709\u5927\u8863 \u4E0D\u8BF4\u4E86 \u6765\u5355\u4E86</p><h2 id="\u6BCF\u59295\u5206\u949F-\u8BA9\u4F60\u5F7B\u5E95\u653E\u5F03\u82F1\u8BED" tabindex="-1"><a class="header-anchor" href="#\u6BCF\u59295\u5206\u949F-\u8BA9\u4F60\u5F7B\u5E95\u653E\u5F03\u82F1\u8BED" aria-hidden="true">#</a> \u6BCF\u59295\u5206\u949F \u8BA9\u4F60\u5F7B\u5E95\u653E\u5F03\u82F1\u8BED</h2><p>\u73B0\u5728\u5F88\u591A\u57F9\u8BAD\u673A\u6784\u3001\u6559\u80B2\u673A\u6784\u559C\u6B22\u6253\u7740 <strong>\u6BCF\u5929n\u5206\u949F \u8BA9\u4F60\u7684\u82F1\u8BED\u600E\u4E48\u6837\u600E\u4E48\u6837</strong>\uFF0C\u8FD9\u57FA\u672C\u53EA\u662F\u5728\u6492\u7CD6\uFF0C\u5148\u628A\u4F60\u54C4\u8FDB\u6765\u518D\u8BF4\u3002 \u82F1\u8BED\u4F5C\u4E3A\u4E00\u95E8\u8BED\u8A00\uFF0C\u672C\u8EAB\u5C31\u4E0D\u53EF\u80FD\u5728\u6781\u77ED\u7684\u65F6\u95F4\u5185\u4E60\u5F97\u3002 \u788E\u7247\u5316\u5B66\u4E60\u57FA\u672C\u4E0A\u5230\u6700\u540E\u8981\u4E48\u653E\u5F03\uFF0C\u8981\u4E48\u5C31\u601D\u7EF4\u53D1\u6563\u5230\u522B\u5904\u53BB\u4E86\uFF08\u6BD4\u5982\u53D1\u73B0\u81EA\u5DF1\u771F\u7684\u5F88\u7231\u770B\u7F8E\u5267\uFF09\u3002 \u5BF9\u4E8E\u5728\u804C\u4EBA\u58EB\uFF0C\u5FC5\u987B\u8981\u5236\u5B9A\u5408\u9002\u7684\u5B66\u4E60\u65B9\u6848\uFF0C\u7EC4\u5408\u5B66\u4E60,\u6700\u7EC8\u624D\u80FD\u7CFB\u7EDF\u5316\u7684\u5B66\u4E60\u82F1\u8BED\u3002</p><p>\u6240\u4EE5\uFF0C\u975E\u5E38\u4E0D\u63A8\u8350\u53BB\u82B1\u8FD9\u90E8\u5206\u51A4\u6789\u94B1\u3002</p><hr><h2 id="kindle\u548Cipad" tabindex="-1"><a class="header-anchor" href="#kindle\u548Cipad" aria-hidden="true">#</a> kindle\u548CiPad</h2><p>\u6211\u77E5\u9053\u73B0\u5728\u5F88\u591A\u4EBA\u7684kindle\u548CiPad\u5728\u5403\u7070\uFF0C\u751A\u81F3\u662F\u5E73\u5E38\u88AB\u7528\u6765\u5F53\u6CE1\u9762\u76D6\u5B50\u3002 \u8FD9\u53EF\u80FD\u662F\u6709\u8DA3\u7684\u73A9\u6CD5\uFF0C\u4E0D\u8FC7\u8FD9\u771F\u7684\u662F\u6211\u4EEC\u8D2D\u4E70\u8FD9\u4E9B\u7535\u5B50\u4EA7\u54C1\u7684\u521D\u8877\u5417\uFF1F</p><p><img src="'+h+'" alt="\u9ED1\u4EBA\u95EE\u53F7.jpg"></p><h2 id="\u4E2A\u4EBA\u60E8\u75DB\u7684\u5B66\u4E60\u7ECF\u5386" tabindex="-1"><a class="header-anchor" href="#\u4E2A\u4EBA\u60E8\u75DB\u7684\u5B66\u4E60\u7ECF\u5386" aria-hidden="true">#</a> \u4E2A\u4EBA\u60E8\u75DB\u7684\u5B66\u4E60\u7ECF\u5386</h2><p>\u521D\u4E2D\u65F6\u671F\uFF0C\u6211\u7684\u6210\u7EE9\u7A33\u7A33\u6392\u5728\u5E74\u7EA7\u7B2C\u4E00\uFF0C\u6211\u5F97\u610F\u5FD8\u5F62\uFF0C\u76EE\u4E2D\u65E0\u4EBA\u3002</p><p>\u521D\u4E8C\u7684\u65F6\u5019\uFF0C\u65B0\u6765\u4E00\u4E2A\u6570\u5B66\u8001\u5E08\uFF0C\u6211\u5B9E\u5728\u65E0\u6CD5\u63A5\u53D7\u4ED6\u628A\u90A3\u4E48\u7B80\u5355\u7684\u6570\u5B66\u9898\u76EE\u8BB2\u9519\uFF0C\u6211\u5BF9\u4ED6\u7684\u6559\u5B66\u4E0D\u5C51\u4E00\u987E\uFF0C\u5B8C\u5168\u4E0D\u628A\u4ED6\u5E03\u7F6E\u7684\u4F5C\u4E1A\u653E\u5728\u5FC3\u4E0A\uFF0C\u4E0A\u8BFE\u65F6\u4E5F\u81EA\u5DF1\u73A9\u81EA\u5DF1\u7684\u3002</p><p>\u76F4\u5230\u6709\u4E00\u5929\u4ED6\u53C8\u53CC\u53D2\u53D5\u8BB2\u9519\u4E86\u4E00\u9053\u9898\uFF0C\u6211\u5FCD\u4E0D\u4F4F\u7B11\u4E86\u51FA\u6765\uFF0C\u5E76\u6447\u4E86\u6447\u5934\uFF0C\u4ED6\u4F30\u8BA1\u662F\u79EF\u6028\u5DF2\u4E45\uFF0C\u53D1\u75AF\u4F3C\u5730\u6253\u6211\uFF0C\u5BF9\u6211\u62F3\u6253\u811A\u8E22\uFF0C\u540E\u6765\u6821\u957F\u77E5\u9053\u6B64\u4E8B\u5C06\u90A3\u4E2A\u8001\u5E08\u5F00\u9664\u4E86\uFF1B</p><p>\u521D\u4E09\uFF0C\u6211\u5C45\u7136\u6545\u4F0E\u91CD\u6F14\uFF0C\u53C8\u5632\u7B11\u4E86\u53E6\u4E00\u4F4D\u6570\u5B66\u8001\u5E08\uFF0C\u8FD9\u6B21\u4E0D\u662F\u56E0\u4E3A\u4ED6\u8BB2\u9519\u4E86\uFF0C\u800C\u662F\u4ED6\u7ED9\u51FA\u7684\u65B9\u6CD5\u4E0D\u662F\u6700\u4F18\u89E3\uFF0C\u6211\u53C8\u53CC\u53D2\u53D5\u88AB\u66B4\u6253\u4E86\u4E00\u987F\uFF0C\u5F53\u65F6\u6211\u7684\u540C\u684C\u90FD\u88AB\u5413\u54ED\u4E86\uFF0C\u800C\u6211\u4F3C\u4E4E\u6CA1\u6709\u611F\u89C9\u5230\u592A\u591A\u7684\u75BC\u75DB\u3002</p><p>\u90A3\u4E2A\u8001\u5E08\u540E\u6765\u627E\u6211\u9053\u6B49\uFF0C\u4ED6\u6253\u81EA\u5DF1\u8033\u5149\uFF0C\u62C9\u7740\u6211\u7684\u624B\u53BB\u9524\u4ED6\uFF0C\u6211\u8BF4&quot;\u4E0A\u4E00\u4E2A\u6253\u6211\u7684\u8001\u5E08\u88AB\u5F00\u9664\u4E86&quot;\uFF0C \u4ED6\u544A\u8BC9\u6211\u8BF4\u4ED6\u7684\u7236\u4EB2\u75C5\u91CD\uFF0C\u4ED6\u7684\u54E5\u54E5\u6E38\u624B\u597D\u95F2\uFF0C\u4ED6\u9700\u8981\u8FD9\u4EFD\u5DE5\u4F5C\uFF0C\u6211\u548C\u4ED6\u8FBE\u6210\u4E86\u534F\u8BAE\uFF1A\u6211\u4E0D\u9700\u8981\u518D\u53BB\u4E0A\u4ED6\u7684\u6570\u5B66\u8BFE\uFF0C\u6211\u53EF\u4EE5\u51FA\u53BB\u73A9\u3002</p><p>\u540E\u6765\u6211\u7684\u786E\u4E5F\u6CA1\u6709\u53BB\u4E0A\u8FC7\u4ED6\u7684\u6570\u5B66\u8BFE\u3002</p><p>\u5728\u6B64\u4E4B\u540E\uFF0C\u6211\u5BF9\u6570\u5B66\u5B8C\u5168\u5931\u53BB\u4E86\u5174\u8DA3\uFF0C\u53C2\u52A0\u5965\u6570\u6BD4\u8D5B\u83B7\u5F97\u7684\u5956\u72B6\u4E5F\u88AB\u6211\u72E0\u72E0\u5730\u6495\u788E\u4E86\u3002</p><p>\u5E74\u5C11\u8F7B\u72C2\u3002\u5475\u3002</p><p>\u5230\u4E86\u9AD8\u4E2D\uFF0C\u5B66\u4E60\u538B\u529B\u771F\u7684\u5F88\u5927\uFF0C\u4E0D\u4EC5\u4EC5\u6765\u6E90\u4E8E\u5916\u754C\uFF0C\u4E5F\u6765\u6E90\u4E8E\u5BF9\u81EA\u5DF1\u63D0\u51FA\u7684\u8FC7\u9AD8\u8981\u6C42\u3002</p><p>\u7531\u4E8E\u6211\u591C\u95F4\u5145\u7535\u65F6\u95F4\u6CA1\u628A\u63E1\u597D\uFF0C\u5BFC\u81F4\u767D\u5929\u56E0\u4E3A\u7CBE\u529B\u4E0D\u8DB3\uFF0C\u4E0A\u8BFE\u6548\u679C\u5F88\u5DEE\uFF0C\u591C\u95F4\u53C8\u65E0\u6CD5\u5C3D\u5FEB\u5165\u7761\uFF0C\u6211\u53C8\u5F00\u59CB\u62C5\u5FC3\u56E0\u4E3A\u4F11\u606F\u65F6\u95F4\u4E0D\u591F\u5BFC\u81F4\u767D\u5929\u840E\u9761\u4E0D\u632F\uFF0C\u4E8E\u662F\u8D8A\u7126\u8651\u8D8A\u7761\u4E0D\u7740\u3002</p><p>\u73ED\u4E3B\u4EFB\u8D23\u5907\u6211\u6210\u7EE9\u4E00\u843D\u5343\u4E08\uFF0C\u95EE\u6211\u662F\u4E0D\u662F\u8C08\u604B\u7231\u3001\u4E0A\u7F51\u6253\u6E38\u620F\u7B49\uFF0C\u7ECF\u5E38\u558A\u6211\u53BB\u529E\u516C\u5BA4\u8C08\u8BDD\uFF08\u5B9E\u9645\u4E0A\u662F\u6279\u8BC4\uFF09\uFF1B</p><p>\u5E74\u7EA7\u4E3B\u4EFB\u4E5F\u53EA\u662F\u5173\u5FC3\u6211\u662F\u4E0D\u662F\u65E9\u4E0A\u53C8\u8FDF\u5230\u4E86\uFF0C\u88AB\u4ED6\u6293\u4F4F\uFF0C\u53C8\u7ACB\u4E00\u529F\uFF1B</p><p>\u6211\u7238\u5F97\u77E5\u6211\u7684\u6210\u7EE9\u843D\u5728\u4E86\u591A\u5C11\u540D\u4E4B\u5916\uFF0C\u4FBF\u518D\u4E5F\u4E0D\u613F\u610F\u6765\u5F00\u5BB6\u957F\u4F1A\uFF1B</p><p>\u4E45\u800C\u4E45\u4E4B\uFF0C\u6076\u6027\u5FAA\u73AF\uFF0C\u6211\u5F00\u59CB\u5931\u7720\uFF0C\u6211\u5BF9\u81EA\u5DF1\u975E\u5E38\u5931\u671B\uFF0C\u6211\u7684\u5EA7\u4F4D\u88AB\u8C03\u5230\u4E86\u7B2C\u4E00\u6392\uFF0C\u6211\u7684\u5E8A\u4F4D\u4E5F\u642C\u8FDB\u4E86\u533B\u9662\uFF1B</p><p>\u6B64\u540E\uFF0C\u6211\u7528\u4E86\u957F\u8FBE2\u5E74\u7684\u65F6\u95F4\u53BB\u6CBB\u7597\u5931\u7720\uFF0C\u90A3\u662F\u4E00\u6BB5\u6697\u65E0\u5929\u65E5\u7684\u65F6\u5149\uFF1B</p><p>\u5728\u8FD9\u6BB5\u75DB\u82E6\u7684\u65F6\u95F4\u5185\uFF0C\u6211\u591A\u6B21\u60F3\u8981\u8F7B\u751F\uFF0C\u8BF4\u5931\u7720\u75DB\u82E6\u5DF2\u7ECF\u662F\u4E00\u79CD\u4EC1\u6148\u7684\u63CF\u8FF0\uFF1B</p><p>\u65E0\u6570\u4E2A\u6E05\u51B7\u7684\u591C\u91CC\uFF0C\u6211\u4E00\u4E2A\u4EBA\u8DD1\u5230\u536B\u751F\u95F4\u83AB\u540D\u7684\u6D41\u4E0B\u773C\u6CEA\uFF0C\u4E0D\u77E5\u9053\u4EBA\u751F\u5230\u5E95\u6709\u4EC0\u4E48\u610F\u4E49\uFF1B</p><p>\u6211\u5E0C\u671B\u6211\u53EF\u4EE5\u7F8E\u7F8E\u7684\u7761\u4E0A\u4E00\u89C9\uFF0C\u53EF\u662F\u6211\u600E\u4E48\u4E5F\u7761\u4E0D\u7740\uFF01</p><p><strong>\u6CA1\u4EBA\u6765\u544A\u8BC9\u6211\uFF0C\u751F\u6D3B\u7684\u610F\u4E49\u5E76\u4E0D\u662F\u4E0E\u4ED6\u4EBA\u4E89\u9AD8\u4E0B\uFF0C\u800C\u5728\u4E8E\u4EAB\u53D7\u52AA\u529B\u5B9E\u73B0\u76EE\u6807\u7684\u8FC7\u7A0B\uFF0C\u7ED3\u679C\u662F\u5BF9\u81EA\u5DF1\u884C\u52A8\u7684\u5609\u5956\u3002</strong></p><p>\u5728\u5931\u7720\u4E4B\u524D\uFF0C\u6211\u90FD\u662F\u7528\u5DE6\u624B\u5199\u5B57\u7684\uFF0C\u5728\u5931\u7720\u671F\u95F4\uFF0C\u4E3A\u4E86\u6253\u53D1\u65E0\u804A\u7684\u65F6\u95F4\uFF0C\u6211\u5B66\u4F1A\u4E86\u7528\u53F3\u624B\u5199\u5B57\uFF0C\u6211\u5F00\u59CB\u7EC3\u4E60\u4E66\u6CD5\uFF0C\u8FD9\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u51CF\u8F7B\u4E86\u6211\u7684\u75DB\u82E6\u3002 \u6211\u628A\u66F4\u591A\u7684\u65F6\u95F4\u6295\u5165\u5230\u7EC3\u4E60\u4E66\u6CD5\u4E2D\u6765\uFF0C\u53C2\u52A0\u4E66\u6CD5\u6BD4\u8D5B\u83B7\u5956\uFF0C\u8BED\u6587\u4F5C\u6587\u591A\u6B21\u5728\u5E74\u7EA7\u8003\u8BD5\u4E2D\u83B7\u5F97\u6EE1\u5206\uFF08\u6211\u8BB0\u5F97\u6700\u6E05\u695A\u7684\u662F\u90A3\u7BC7\u6279\u8BC4\u5E94\u8BD5\u6559\u80B2\u7684\uFF09\uFF1B</p><p>\u6211\u5F00\u59CB\u4F53\u4F1A\u5230\u4E86\u4E00\u4E9B\u4E0D\u4E00\u6837\u7684\u4E1C\u897F\uFF0C\u591C\u91CC\u6211\u5F00\u59CB\u72AF\u56F0\uFF0C\u6211\u7761\u5F97\u7740\u4E86\u3002</p><p>\u5728\u4E4B\u540E\u6211\u7684\u6210\u7EE9\u4E5F\u6E10\u6E10\u63D0\u9AD8\u4E86\u4E00\u4E9B\uFF0C\u867D\u7136\u9AD8\u8003\u6211\u5F88\u5931\u8D25\uFF0C\u9760\u7740\u8BED\u6587\u548C\u82F1\u8BED\u9AD8\u5206\u52C9\u5F3A\u4E0A\u4E86\u4E00\u672C\uFF08\u5230\u9AD8\u4E2D\u540E\u6211\u5C31\u57FA\u672C\u4E0D\u518D\u78B0\u6570\u5B66\u4E86\uFF09\u3002</p><p>\u6240\u4EE5\uFF0C\u5F53\u4F60\u5904\u4E8E\u4EBA\u751F\u4F4E\u8C37\u65F6\uFF0C\u4E0D\u59A8\u8BD5\u8BD5\u5B66\u4E60\u65B0\u7684\u4E1C\u897F\uFF0C\u5F88\u53EF\u80FD\u4F1A\u4ECE\u8FD9\u4E9B\u4E8B\u60C5\u91CC\u627E\u5230\u4E0D\u4E00\u6837\u7684\u4E50\u8DA3\u3002</p><p><strong>\u8981\u5B66\u4F1A\u653E\u5F03\uFF0C\u5B66\u4F1A\u63A5\u53D7\u81EA\u5DF1\uFF0C\u5B66\u4F1A\u627E\u5230\u81EA\u5DF1\u771F\u6B63\u559C\u6B22\u7684\u4E8B\u60C5\u3002\u5982\u679C\u67D0\u4EF6\u4E8B\u60C5\u662F\u4F60\u5DE5\u4F5C\u548C\u751F\u6D3B\u6240\u4F9D\u8D56\u7684\uFF0C\u90A3\u4E48\u8BD5\u7740\u53BB\u559C\u6B22\u5979\u5427\u3002</strong></p><p>\u8FD9\u4E5F\u662F\u6211\u5206\u4EAB\u81EA\u5DF1\u5B66\u4E60\u7ECF\u9A8C\u7684\u521D\u8877\uFF0C\u6211\u5E0C\u671B\u5927\u5BB6\u80FD\u5C11\u8D70\u5F2F\u8DEF\uFF0C\u91CD\u65B0\u8BA4\u8BC6\u4E00\u4E9B\u4E8B\u60C5\u3002</p><hr><p>\u5927\u5B66\u671F\u95F4\uFF0C\u82F1\u8BED\u8001\u5E08\u8DDF\u6211\u6760\u4E0A\u4E86\uFF0C\u4E3B\u8981\u7684\u539F\u56E0\u662F\u67D0\u8282\u8BFE\u6211\u6CA1\u6709\u5E26\u8BFE\u672C\uFF0C\u5979\u63D0\u6211\u62FC\u5199\uFF0C\u800C\u6211\u5C45\u7136\u5168\u5199\u5BF9\u4E86\u3002</p><p>\u5979\u7528\u82F1\u8BED\u8868\u626C\u4E86\u6211\uFF0C\u6211\u4E5F\u7528\u82F1\u8BED\u88C5\u4E86\u4E00\u56DEx\uFF0C\u57FA\u672C\u6D41\u5229\u5730\u4E0E\u5979\u5B8C\u6210\u4E86\u4E00\u6B21\u53E3\u8BED\u5BF9\u8BDD\u3002</p><p>\u90A3\u8282\u8BFE\u4E0A\uFF0C\u5168\u5E74\u7EA7\u7684\u4EBA\u90FD\u5411\u6211\u6295\u6765\u7FA1\u6155\uFF08\u5176\u5B9E\u662F\u9119\u5937\uFF09\u7684\u76EE\u5149\uFF0C\u73B0\u5728\u56DE\u60F3\u8D77\u6765\u90FD\u8FD8\u6709\u4E9B\u5F97\u610F\u5462\u3002</p><p><strong>\u4E4B\u540E\uFF0C\u5979\u76EF\u4E0A\u4E86\u6211\uFF0C\u6211\u6CA1\u6709\u4E00\u6B21\u662F\u80FD\u6210\u529F\u8EB2\u8FC7\u5979\u7684\u70B9\u540D\uFF01</strong></p><p>\u4F4D\u5217\u5E74\u7EA7\u56DB\u5927\u9003\u8BFE\u4E4B\u9996\u7684\u6211\uFF0C\u522B\u63D0\u591A\u6068\u5979\u4E86\u3002</p><p>\u540E\u6765\uFF0C<strong>\u5728\u82F1\u8BED\u8001\u5E08\u7684\u52AA\u529B\u4E0B\uFF0C\u6211\u4EEC\u7CFB\u6240\u6709\u7684\u8001\u5E08\u90FD\u76EF\u4E0A\u4E86\u6211</strong>\uFF0C\u6BCF\u6B21\u70B9\u540D\u90FD\u5148\u95EE2\u73ED\u7684xxx\u6765\u4E86\u6CA1\u6709\uFF0C\u5982\u679C\u6211\u6765\u4E86\uFF0C\u90A3\u4E48\u8FD9\u8282\u8BFE\u5C31\u4E0D\u7528\u518D\u70B9\u540D\u4E86\u3002</p><p>\u4E8E\u662F\uFF0C\u5230\u5927\u4E09\u4E0B\u5B66\u671F\uFF0C\u6211\u6302\u4E8619\u95E8\u8BFE\u3002</p><p>\u5927\u56DB\u5F00\u5B66\uFF0C\u5B66\u6821\u8981\u6C42\u5C06\u6211\u964D\u7EA7\u9000\u5230\u5927\u4E09\uFF0C\u773C\u770B\u6BD5\u4E1A\u65E0\u671B\u3002</p><p>\u7ED3\u679C\u82F1\u8BED\u8001\u5E08\u5E2E\u6211\u627E\u73ED\u4E3B\u4EFB\u3001\u7CFB\u4E3B\u4EFB\u3001\u6559\u52A1\u5904\uFF0C\u575A\u6301\u8BA9\u6211\u7559\u5728\u539F\u5E74\u7EA7\u5B66\u4E60\uFF0C\u4E5F\u5C31\u662F\u4E00\u6B21\u91CD\u4FEE19\u95E8\u8BFE\u4E0D\u8FC7\u7684\u8BDD\u5C31\u76F4\u63A5\u8084\u4E1A\uFF0C\u795Etm\u7684\u4E2D\u56FD\u597D\u8001\u5E08\u3002</p><p>\u5927\u56DB\uFF0C\u6211\u80CC\u7740\u6C89\u6C89\u7684\u4E66\u5305\uFF0C\u6BCF\u5929\u53BB\u6559\u5BA4\u548C\u56FE\u4E66\u9986\u81EA\u4E60\u3002\u6BCF\u4E2A\u56DE\u5BBF\u820D\u7684\u508D\u665A\uFF0C\u6211\u90FD\u8981\u72E0\u72E0\u5730\u6068\u4E00\u904D\u6211\u7684\u82F1\u8BED\u8001\u5E08\u3002</p><p>\u600E\u4E48\u4F1A\u6709\u8FD9\u6837\u4E27\u5FC3\u75C5\u72C2\u7684\u8001\u5E08\u5462\uFF1F</p><p>\u671F\u672B\u8003\u8BD5\uFF0C\u517126\u95E8\u8BFE\uFF0C\u6211\u8EAB\u5FC3\u4FF1\u75B2\uFF0C\u53EB\u82E6\u4E0D\u8FED\u3002</p><p>\u5728\u5F97\u77E5\u91CD\u4FEE\u768419\u95E8\u8BFE\u5168\u8FC7\u4E86\u4E4B\u540E\uFF0C\u6211\u5F00\u5FC3\u7684\u773C\u6CEA\u90FD\u5FEB\u6389\u4E0B\u6765\u3002\u7EC8\u4E8E\u53EF\u4EE5\u72E0\u72E0\u5730\u628A\u8138\u6253\u56DE\u53BB\u4E86\uFF0C\u4F60\u4E2B\u7684\u4E0D\u5C31\u662F\u60F3\u770B\u6211\u6BD5\u4E0D\u4E86\u4E1A\u7684\u7B11\u8BDD\u4E48\uFF01</p><p>\u7136\u540E\u6211\u5728\u7ED9\u81EA\u5DF1\u7684\u6BD5\u4E1A\u5BC4\u8BED\u4E0A\u5199\u4E0B\u4E00\u53E5\u8BDD\uFF1A\u201C\u5927\u5B66\u671F\u95F4\uFF0C\u5343\u4E07\u522B\u8DDF\u81EA\u5DF1\u7684\u8001\u5E08\u8C08\u604B\u7231\uFF01\u201D</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p>\u5176\u5B9E\u5199\u4E86\u8FD9\u4E48\u591A\uFF0C\u6211\u603B\u7ED3\u4E0B\uFF0C\u5B66\u4E60\u82F1\u8BED\u7684\u4E3B\u8981\u65B9\u6CD5\u8FD8\u662F\u9760\u5929\u8D4B\u3002</p><p>\u52AA\u529B\uFF0C\u662F\u6700\u597D\u7684\u5929\u8D4B\uFF01</p><blockquote><p>\u5173\u4E8E\u88AB\u8001\u5E08\u66B4\u6253\uFF0C\u66F4\u591A\u7684\u95EE\u9898\u5728\u6211\uFF0C\u6CA1\u6709\u987E\u53CA\u8001\u5E08\u7684\u5C0A\u4E25\uFF0C\u81EA\u4EE5\u4E3A\u662F\uFF0C\u90A3\u65F6\u7684\u6211\uFF0C\u5F88\u4E0D\u50CF\u8BDD\u3002 \u4EE5\u4E0A\u7EAF\u5C5E\u626F\u6DE1,\u53EA\u4E3A\u535A\u541B\u4E00\u7B11\u3002</p></blockquote>',71),u=e("\u4E0B\u4E00\u7BC7\uFF1A"),f=e("\u6211\u548C\u6211\u7684\u524D\u5973\u53CB\u4EEC");function g(m,b){const p=d("RouterLink");return t(),o("div",null,[l,n("p",null,[u,r(p,{to:"/part-3/what-is-love-1.html"},{default:i(()=>[f]),_:1})])])}var k=a(c,[["render",g],["__file","x-misc.html.vue"]]);export{k as default};
